using Xunit;

namespace Tests
{
    public class Tests
    {
        [Fact]
        public void CanTellIfZero()
        {
            byte numberByte = 0;
            short numberShort = 0;
            int numerInt = 0;
            long numberLong = 0L;
            float numberFloat = 0.0F;
            double numberDouble = 0.0D;
            decimal numberDecimal = 0;

            Assert.True(IsZero.Check(numberByte));
            Assert.True(IsZero.Check(numberShort));
            Assert.True(IsZero.Check(numerInt));
            Assert.True(IsZero.Check(numberLong));
            Assert.True(IsZero.Check(numberFloat));
            Assert.True(IsZero.Check(numberDouble));
            Assert.True(IsZero.Check(numberDecimal));
        }

        [Fact]
        public void CanTellIfNotZero()
        {
            byte numberByte = 123;
            short numberShort = 123;
            int numerInt = 123;
            long numberLong = 123;
            float numberFloat = 2113.001001010101F;
            double numberDouble = 0.;
            decimal numberDecimal = 1;

            Assert.False(IsZero.Check(numberByte));
            Assert.False(IsZero.Check(numberShort));
            Assert.False(IsZero.Check(numerInt));
            Assert.False(IsZero.Check(numberLong));
            Assert.False(IsZero.Check(numberFloat));
            Assert.False(IsZero.Check(numberDouble));
            Assert.False(IsZero.Check(numberDecimal));
        }
    }
}